plot_mutations = Plot.plot({
  y: {grid: true, label: "Nombre de transactions"},
  x: {
    ticks: 12,
    transform: (d) =&gt; Math.pow(10, d),
    type: "log",
    tickFormat: "~s",
    label: "Prix (√©chelle log) ‚Üí"
  },
  marks: [
    Plot.rectY(
      dvf.filter(d =&gt; d.valeur_fonciere &gt; 10000),
      Plot.binX({y: "count"},
      {
        x: d =&gt; Math.log10(d.valeur_fonciere),
        tip: true
      })
    ),
    Plot.ruleY([0])
  ]
})
















